import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Lock, Eye, Mail } from 'lucide-react';
import { authService } from '../services/api';

const Login = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // For demo purposes, we'll simulate a successful login
      // const response = await authService.login(credentials);
      // localStorage.setItem('token', response.data.token);

      // Simulated login for demo
      localStorage.setItem('token', 'demo-token');

      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to login. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Basic inline styles that don't rely on Tailwind
  const styles = {
    container: {
      padding: '20px',
      maxWidth: '600px',
      margin: '0 auto',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      marginBottom: '20px'
    },
    logoTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      marginTop: '10px',
      marginBottom: '20px'
    },
    welcomeTitle: {
      fontSize: '32px',
      fontWeight: 'bold',
      marginBottom: '10px'
    },
    subtitle: {
      marginBottom: '20px'
    },
    securityItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      marginBottom: '8px'
    },
    loginSection: {
      marginTop: '40px',
    },
    loginTitle: {
      fontSize: '24px',
      fontWeight: 'bold',
      marginBottom: '10px'
    },
    socialButtons: {
      display: 'flex',
      gap: '10px',
      marginBottom: '20px'
    },
    socialButton: {
      padding: '5px 10px',
      border: '1px solid #ccc',
      background: 'none',
      cursor: 'pointer'
    },
    divider: {
      margin: '15px 0',
    },
    formGroup: {
      marginBottom: '15px'
    },
    label: {
      display: 'block',
      marginBottom: '5px'
    },
    inputWrapper: {
      display: 'flex',
      alignItems: 'center'
    },
    input: {
      border: '1px solid #ccc',
      padding: '5px',
      width: '100%'
    },
    rememberForgot: {
      display: 'flex',
      justifyContent: 'space-between',
      marginBottom: '15px'
    },
    submitButton: {
      padding: '5px 10px',
      marginBottom: '20px',
      cursor: 'pointer'
    },
    link: {
      color: 'blue',
      textDecoration: 'underline',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div>üìã</div>
        <div style={styles.logoTitle}>HRSystem</div>
      </div>

      <div>
        <h1 style={styles.welcomeTitle}>Welcome to HR Management System</h1>
        <p style={styles.subtitle}>Streamline your HR operations with our comprehensive management solution.</p>

        <div style={styles.securityItem}>
          <span>üõ°Ô∏è</span>
          <span>Enterprise-grade security</span>
        </div>

        <div style={styles.securityItem}>
          <span>üîí</span>
          <span>ISO 27001 Certified</span>
        </div>

        <div style={styles.securityItem}>
          <span>üõ°Ô∏è</span>
          <span>GDPR Compliant</span>
        </div>
      </div>

      <div style={styles.loginSection}>
        <h2 style={styles.loginTitle}>Sign in to your account</h2>
        <p>Enter your credentials to access your account</p>

        {error && (
          <div style={{ color: 'red', marginBottom: '15px' }}>
            {error}
          </div>
        )}

        <div style={styles.socialButtons}>
          <button style={styles.socialButton}>
            <span>G</span> Google
          </button>
          <button style={styles.socialButton}>
            <span>M</span> Microsoft
          </button>
        </div>

        <div style={styles.divider}>
          or continue with
        </div>

        <form onSubmit={handleSubmit}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Email address</label>
            <div style={styles.inputWrapper}>
              <span style={{ marginRight: '8px' }}>‚úâÔ∏è</span>
              <input
                type="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                style={styles.input}
                placeholder="Enter your email"
                required
              />
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Password</label>
            <div style={styles.inputWrapper}>
              <span style={{ marginRight: '8px' }}>üîí</span>
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                value={credentials.password}
                onChange={handleChange}
                style={styles.input}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                required
              />
              <span style={{ marginLeft: '8px', cursor: 'pointer' }} onClick={() => setShowPassword(!showPassword)}>
                üëÅÔ∏è
              </span>
            </div>
          </div>

          <div style={styles.rememberForgot}>
            <div>
              <input
                id="remember-me"
                name="remember-me"
                type="checkbox"
                checked={rememberMe}
                onChange={() => setRememberMe(!rememberMe)}
                style={{ marginRight: '5px' }}
              />
              <label htmlFor="remember-me">Remember me</label>
            </div>
            <a href="#" style={styles.link}>Forgot password?</a>
          </div>

          <button
            type="submit"
            style={styles.submitButton}
            disabled={loading}
          >
            {loading ? 'Signing in...' : 'Sign in'}
          </button>
        </form>

        <div>
          Don't have an account? <a href="#" style={styles.link}>Contact your HR admin</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
