import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Mail, Eye, Lock, Shield } from 'lucide-react';
import { authService } from '../services/api';

const Login = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // For demo purposes, we'll simulate a successful login
      // const response = await authService.login(credentials);
      // localStorage.setItem('token', response.data.token);

      // Simulated login for demo
      localStorage.setItem('token', 'demo-token');

      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to login. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-900">
      {/* Left Panel */}
      <div className="hidden lg:flex lg:w-1/2 flex-col p-12 justify-between">
        <div>
          <div className="flex items-center gap-2 mb-12">
            <div className="bg-green-500 p-2 rounded-md">
              <Shield className="text-white h-5 w-5" />
            </div>
            <h2 className="text-white text-xl font-bold">HRSystem</h2>
          </div>

          <div className="mt-20">
            <h1 className="text-white text-4xl font-bold mb-4">Welcome to HR Management System</h1>
            <p className="text-gray-400">Streamline your HR operations with our comprehensive management solution.</p>
          </div>
        </div>

        <div className="mb-8">
          <div className="flex items-center gap-2 mb-8 text-gray-400">
            <Shield className="h-5 w-5" />
            <span>Enterprise-grade security</span>
          </div>

          <div className="flex gap-4">
            <div className="border border-gray-700 rounded-md px-4 py-2 flex items-center gap-2">
              <Lock className="text-green-500 h-5 w-5" />
              <span className="text-white text-sm">ISO 27001 Certified</span>
            </div>
            <div className="border border-gray-700 rounded-md px-4 py-2 flex items-center gap-2">
              <Shield className="text-green-500 h-5 w-5" />
              <span className="text-white text-sm">GDPR Compliant</span>
            </div>
          </div>
        </div>
      </div>

      {/* Right Panel - Login Form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-6">
        <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-md">
          <div className="text-center mb-6">
            <h1 className="text-2xl font-bold text-gray-900">Sign in to your account</h1>
            <p className="text-gray-600 mt-2">Enter your credentials to access your account</p>
          </div>

          {error && (
            <div className="bg-red-100 text-red-700 p-3 rounded-md mb-4">
              {error}
            </div>
          )}

          {/* Social Login Options */}
          <div className="grid grid-cols-2 gap-4 mb-6">
            <button className="flex items-center justify-center gap-2 border border-gray-300 rounded-md py-2 px-4 hover:bg-gray-50">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" fill="#4285F4"/>
                <path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" fill="#34A853"/>
                <path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" fill="#FBBC05"/>
                <path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" fill="#EA4335"/>
              </svg>
              <span>Google</span>
            </button>
            <button className="flex items-center justify-center gap-2 border border-gray-300 rounded-md py-2 px-4 hover:bg-gray-50">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
                <path d="M20.965 7.096c-2.783-1.66-5.318-.615-7.964.422-2.646-1.037-5.181-2.082-7.965-.422C2.084 8.756 2 13.096 2 16.512c0 1.014.05 1.9.152 2.645 3.877-.194 5.19-2.03 6.904-4.318.416-.557 1.63.129 1.16.802-1.874 2.703-3.396 5.951-7.404 6.154.128.103.264.193.408.278 4.513 2.742 8.142-1.29 8.778-1.975.637.684 4.266 4.717 8.78 1.975.143-.085.28-.175.407-.278-4.008-.203-5.53-3.451-7.405-6.154-.47-.673.745-1.359 1.162-.802 1.714 2.288 3.027 4.125 6.903 4.318.1-.746.152-1.631.152-2.645 0-3.416-.084-7.756-3.034-9.416zM8.556 8.464c.682 0 1.235.553 1.235 1.235 0 .682-.553 1.235-1.235 1.235-.682 0-1.235-.553-1.235-1.235 0-.682.553-1.235 1.235-1.235zm6.889 0c.682 0 1.235.553 1.235 1.235 0 .682-.553 1.235-1.235 1.235-.682 0-1.235-.553-1.235-1.235 0-.682.553-1.235 1.235-1.235z"/>
              </svg>
              <span>Microsoft</span>
            </button>
          </div>

          <div className="relative my-6">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-gray-300"></div>
            </div>
            <div className="relative flex justify-center text-sm">
              <span className="px-2 bg-white text-gray-500">or continue with</span>
            </div>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">Email address</label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Mail className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  id="email"
                  type="email"
                  name="email"
                  value={credentials.email}
                  onChange={handleChange}
                  className="input pl-10"
                  placeholder="Enter your email"
                  required
                />
              </div>
            </div>

            <div className="mb-6">
              <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  id="password"
                  type="password"
                  name="password"
                  value={credentials.password}
                  onChange={handleChange}
                  className="input pl-10"
                  placeholder="•••••••"
                  required
                />
                <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                  <Eye className="h-5 w-5 text-gray-400 cursor-pointer" />
                </div>
              </div>
            </div>

            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center">
                <input
                  id="remember-me"
                  name="remember-me"
                  type="checkbox"
                  checked={rememberMe}
                  onChange={() => setRememberMe(!rememberMe)}
                  className="h-4 w-4 text-green-500 focus:ring-green-500 border-gray-300 rounded"
                />
                <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-700">
                  Remember me
                </label>
              </div>
              <div className="text-sm">
                <a href="#" className="font-medium text-green-500 hover:text-green-600">
                  Forgot password?
                </a>
              </div>
            </div>

            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-500 hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
              disabled={loading}
            >
              {loading ? 'Signing in...' : 'Sign in'}
            </button>
          </form>

          <div className="mt-6 text-center text-sm text-gray-600">
            <p>Don't have an account? <a href="#" className="text-green-500 hover:text-green-600">Contact your HR admin</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
