import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Lock, Eye } from 'lucide-react';
import { authService } from '../services/api';

const Login = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // For demo purposes, we'll simulate a successful login
      // const response = await authService.login(credentials);
      // localStorage.setItem('token', response.data.token);

      // Simulated login for demo
      localStorage.setItem('token', 'demo-token');

      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to login. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-white">
      <div className="max-w-md mx-auto">
        {/* Logo and Header */}
        <div className="mb-6">
          <Shield className="w-6 h-6 mb-2" />
          <h1 className="text-2xl font-bold">HRSystem</h1>
        </div>

        {/* Welcome Text */}
        <div className="mb-6">
          <h2 className="text-3xl font-bold mb-2">Welcome to HR Management System</h2>
          <p className="mb-4">Streamline your HR operations with our comprehensive management solution.</p>

          <div className="flex items-center gap-2 mb-2">
            <Shield className="w-5 h-5" />
            <span>Enterprise-grade security</span>
          </div>

          <div className="flex flex-col gap-2">
            <div className="flex items-center gap-2">
              <Lock className="w-5 h-5" />
              <span>ISO 27001 Certified</span>
            </div>
            <div className="flex items-center gap-2">
              <Shield className="w-5 h-5" />
              <span>GDPR Compliant</span>
            </div>
          </div>
        </div>

        {/* Login Form */}
        <div className="mt-8">
          <h2 className="text-2xl font-bold mb-2">Sign in to your account</h2>
          <p className="mb-4">Enter your credentials to access your account</p>

          {error && (
            <div className="bg-red-100 text-red-700 p-3 rounded-md mb-4">
              {error}
            </div>
          )}

          {/* Social Login Options */}
          <div className="flex gap-2 mb-4">
            <button className="border px-3 py-1 flex items-center gap-2">
              <span>G</span> Google
            </button>
            <button className="border px-3 py-1 flex items-center gap-2">
              <span>M</span> Microsoft
            </button>
          </div>

          <div className="mb-4">
            <p>or continue with</p>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block mb-1">Email address</label>
              <div className="flex items-center">
                <span className="mr-2">✉️</span>
                <input
                  type="email"
                  name="email"
                  value={credentials.email}
                  onChange={handleChange}
                  className="border px-2 py-1 w-full"
                  placeholder="Enter your email"
                  required
                />
              </div>
            </div>

            <div className="mb-4">
              <label className="block mb-1">Password</label>
              <div className="flex items-center">
                <span className="mr-2">🔒</span>
                <input
                  type="password"
                  name="password"
                  value={credentials.password}
                  onChange={handleChange}
                  className="border px-2 py-1 w-full"
                  placeholder="••••••"
                  required
                />
                <Eye className="w-5 h-5 ml-2" />
              </div>
            </div>

            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center">
                <input
                  id="remember-me"
                  name="remember-me"
                  type="checkbox"
                  checked={rememberMe}
                  onChange={() => setRememberMe(!rememberMe)}
                  className="mr-2"
                />
                <label htmlFor="remember-me">Remember me</label>
              </div>
              <a href="#" className="text-blue-600">Forgot password?</a>
            </div>

            <button
              type="submit"
              className="border px-3 py-1 mb-4"
              disabled={loading}
            >
              {loading ? 'Signing in...' : 'Sign in'}
            </button>
          </form>

          <div>
            <p>Don't have an account? <a href="#" className="text-blue-600">Contact your HR admin</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
