import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { 
  Home, 
  Users, 
  Building2, 
  Briefcase, 
  Settings, 
  LogOut, 
  Menu, 
  X,
  ChevronDown,
  Bell
} from 'lucide-react';
import { authService } from '../../services/api';

const DashboardLayout = ({ children }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleUserMenu = () => {
    setIsUserMenuOpen(!isUserMenuOpen);
  };

  const handleLogout = async () => {
    await authService.logout();
    window.dispatchEvent(new Event('storage'));
    navigate('/login');
  };

  const navItems = [
    { path: '/dashboard', icon: <Home size={20} />, label: 'Dashboard' },
    { path: '/employees', icon: <Users size={20} />, label: 'Employee Management' },
    { path: '/departments', icon: <Building2 size={20} />, label: 'Department Management' },
    { path: '/jobs', icon: <Briefcase size={20} />, label: 'Job Management' },
    { path: '/settings', icon: <Settings size={20} />, label: 'Settings' },
  ];

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <div className="min-h-screen flex bg-gray-50">
      {/* Sidebar */}
      <aside className="w-64 bg-[#0B1B24] text-white min-h-screen">
        <div className="p-5 flex items-center gap-2 border-b border-gray-700">
          <div className="w-8 h-8 bg-[#26D07C] rounded flex items-center justify-center">
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M19 3H5C3.9 3 3 3.9 3 5V19C3 20.1 3.9 21 5 21H19C20.1 21 21 20.1 21 19V5C21 3.9 20.1 3 19 3ZM5 5H9V19H5V5ZM19 19H11V5H19V19Z" fill="white"/>
            </svg>
          </div>
          <span className="text-lg font-bold">HRSystem</span>
        </div>

        <nav className="mt-6">
          <ul>
            {navItems.map((item) => (
              <li key={item.path} className="mb-1">
                <Link 
                  to={item.path}
                  className={`
                    flex items-center px-5 py-3 text-sm
                    ${isActive(item.path) 
                      ? 'bg-[#26D07C]/10 text-[#26D07C] border-l-4 border-[#26D07C]' 
                      : 'text-gray-300 hover:bg-white/5 border-l-4 border-transparent'}
                  `}
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  <span className="mr-3">{item.icon}</span>
                  {item.label}
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        <div className="absolute bottom-0 w-64 p-5 border-t border-gray-700">
          <button 
            onClick={handleLogout}
            className="flex items-center text-gray-300 hover:text-white"
          >
            <LogOut size={20} className="mr-3" />
            Logout
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Header */}
        <header className="bg-white shadow-sm p-4 flex justify-between items-center">
          <h1 className="text-xl font-semibold">Dashboard Overview</h1>

          <div className="flex items-center gap-4">
            <button className="text-gray-500 relative">
              <Bell size={20} />
              <span className="absolute -top-1 -right-1 bg-red-500 rounded-full w-4 h-4 flex items-center justify-center text-white text-xs">3</span>
            </button>

            <div className="relative">
              <button 
                className="flex items-center gap-2"
                onClick={toggleUserMenu}
              >
                <div className="w-8 h-8 rounded-full bg-blue-500 flex items-center justify-center text-white">
                  JD
                </div>
                <span className="text-sm font-medium">John Doe</span>
                <ChevronDown size={16} />
              </button>

              {isUserMenuOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-10 border border-gray-200">
                  <Link to="/settings" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Settings</Link>
                  <button 
                    onClick={handleLogout}
                    className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                  >
                    Sign out
                  </button>
                </div>
              )}
            </div>
          </div>
        </header>

        {/* Main Content Area */}
        <main className="flex-1 p-6 bg-gray-50 overflow-auto">
          {children}
        </main>
      </div>

      {/* Mobile Menu Toggle */}
      <button 
        className="fixed bottom-4 right-4 md:hidden bg-primary-600 text-white p-3 rounded-full shadow-lg"
        onClick={toggleMobileMenu}
      >
        {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
      </button>
    </div>
  );
};

export default DashboardLayout;
