        </button>
      </div>

      {/* Success message */}
      {successMessage && (
        <div className="bg-green-50 text-green-700 p-3 rounded-md mb-4 flex justify-between items-center">
          <p>{successMessage}</p>
          <button 
            className="text-green-500"
            onClick={() => setSuccessMessage('')}
          >
            <X size={18} />
