import React, { useState } from 'react';
import { X } from 'lucide-react';
import { employeeService } from '../../services/api';

const AddEmployeeModal = ({ isOpen, onClose, onSuccess, departments }) => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    department_id: '',
    job_id: '',
    hire_date: '',
    salary: '',
    bonus: '',
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  // Debugging - Check if departments are passed correctly
  console.log("Departments in AddEmployeeModal:", departments);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.first_name) newErrors.first_name = 'First name is required';
    if (!formData.last_name) newErrors.last_name = 'Last name is required';
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }
    if (!formData.department_id) newErrors.department_id = 'Department is required';
    if (!formData.job_id) newErrors.job_id = 'Job title is required';
    if (!formData.hire_date) newErrors.hire_date = 'Start date is required';
    if (!formData.salary || isNaN(formData.salary) || Number(formData.salary) <= 0) {
      newErrors.salary = 'Salary must be a positive number';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);

    try {
      const employeeData = {
        ...formData,
        salary: Number(formData.salary),
        bonus: formData.bonus ? Number(formData.bonus) : null,
      };

      const response = await employeeService.create(employeeData);

      if (response.data.success) {
        onSuccess(response.data.employee);
        onClose();
        setFormData({
          first_name: '',
          last_name: '',
          email: '',
          phone: '',
          department_id: '',
          job_id: '',
          hire_date: '',
          salary: '',
          bonus: '',
        });
      }
    } catch (error) {
      console.error('Error creating employee:', error);
      setErrors({ general: 'An error occurred. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-2xl">
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-bold">Add New Employee</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-4">
          {errors.general && (
            <div className="mb-4 p-3 bg-red-50 text-red-500 rounded-md">
              {errors.general}
            </div>
          )}

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">First Name</label>
            <input
              type="text"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
              className="w-full p-2 border rounded"
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Department</label>
            <select
              name="department_id"
              value={formData.department_id}
              onChange={handleChange}
              className="w-full p-2 border rounded"
            >
              <option value="">Select Department</option>
              {departments.length > 0 ? (
                departments.map(dept => (
                  <option key={dept.id} value={dept.id}>{dept.name}</option>
                ))
              ) : (
                <option value="" disabled>No departments available</option>
              )}
            </select>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Job Title</label>
            <select
              name="job_id"
              value={formData.job_id}
              onChange={handleChange}
              className="w-full p-2 border rounded"
            >
              <option value="">Select Job</option>
              {jobs.length > 0 ? (
                jobs.map(job => (
                  <option key={job.id} value={job.id}>{job.title}</option>
                ))
              ) : (
                <option value="" disabled>No jobs available</option>
              )}
            </select>
          </div>

          <div className="flex justify-end mt-4">
            <button type="button" onClick={onClose} className="px-4 py-2 text-gray-600 border rounded-md">
              Cancel
            </button>
            <button type="submit" disabled={loading} className="px-4 py-2 bg-green-500 text-white rounded-md">
              {loading ? 'Adding...' : 'Add Employee'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddEmployeeModal;
